import React from "react";



function Home() {
    const [data, setData] = React.useState(null);
    const [Categories, setCategories] = React.useState(['null']);
    React.useEffect(() => {
        fetch("/api")
            .then((res) => res.json())
            .then((data) => setData(data.message));

        fetch("/api/category")
            .then((res) => res.json())
            .then((Categories) => {
                setCategories(Categories.message)
                let temp = Categories.message;
                console.log(temp)
                let i = 0;
                for (i = 0; i < temp.length; i++) {
                    temp[i] = `<option key="${temp[i].charAt(0)}">${temp[i].substring(1)}</option>`
                }
                document.getElementById('select-category').innerHTML = '<option>Select Category</option>';
                document.getElementById('select-category').innerHTML += temp;
            })
    }, []);

